```{r, echo=FALSE, warning=FALSE, message=FALSE, results='hide'}
# Run this and use output in qmd file

randDispOrd <- sample(nrow(studentscsv))

for (i in 1:nrow(studentscsv)) {
  # output name and title
  cat(paste0("# ", studentscsv$name[randDispOrd[i]], "\n"))
  cat(paste0("**", studentscsv$position[randDispOrd[i]], "**\n\n"))
  # output the summary in a grid format
  cat(":::: {.grid}\n\n")
  if (i %% 2 == 0) {
    cat("::: {.g-col-8}\n")
    cat(paste0("![](images/", studentscsv$image[randDispOrd[i]], ")\n"))
  } else {
    cat("::: {.g-col-4}\n")
    cat(studentscsv$summary[randDispOrd[i]], "\n")
  }
  cat(":::\n\n")
  if (i %% 2 == 0) {
    cat("::: {.g-col-4}\n")
    cat(studentscsv$summary[randDispOrd[i]], "\n")
  } else {
    cat("::: {.g-col-8}\n")
    cat(paste0("![](images/", studentscsv$image[randDispOrd[i]], ")\n"))
  }
  cat(":::\n\n")
  cat("::::\n\n")
  cat("---\n\n")
}
```
